<?php  require_once('db.php');if(isset($_POST['type']))	$type=$_POST['type']; if($type=='basetext')	echo getBaseTextList();else	echo getUserTextList();	function getBaseTextList(){  	$res=dbRes("SELECT * FROM `basetext` WHERE head!='' ORDER BY `fileid`,`filename`");	$basetext_list="";	while($row=mysql_fetch_array($res)){		$filename=$row['filename'];		if (strpos($filename,'-') === false) {			$basetext_list.='<li><input name="fileselect" type="checkbox" value="'.$row['fileid'].'" />'. $filename.'</li>';		} else {			$basetext_list.='<li><input name="fileselect" type="checkbox" value="'.$row['fileid'].'" />'. substr($filename,strpos($filename,'-')+1).'</li>';		}	}	if($basetext_list!='')		$basetext_list='Base text:<ul style="list-style-type:none; margin:0; padding:0">'.$basetext_list.'</ul>';			return $basetext_list;}function getUserTextList(){ 	$userid=$_POST['userid'];	if($userid==''){		return;	} 		$res=dbRes("SELECT * FROM `usertext` WHERE head!='' AND `userid`='".$userid."' ORDER BY `id` ASC");	$usertext_list="";	while($row=mysql_fetch_array($res)){		$usertext_list.='<li><input name="fileselect" type="radio" value="'.$row['filename'].'" />'. $row['filename'].'</li>';	}	if($usertext_list!='')		$usertext_list='<ul style="list-style-type:none; margin:0; padding:0">'.$usertext_list.'</ul>';	return $usertext_list;}?>